descriptor.controllerScript:
self.onInit = function() {
    var map = $('#TimeseriesPolygonMap');
    console.log(self.ctx);
    self.ctx.map = new TbMapWidgetV2('openstreet-map',
        false, self.ctx, undefined, map);
    self.mapleaflet = self.ctx.map.map.map;
    //self.mapleaflet.panBy([200, 300]);    
    self.mapleaflet.setView([32, 34.95], 9);
    // self.resizeMap();
}

self.resizeMap = function() {
    self.mapleaflet._container.style.height = (self.ctx
        .height - 30) + "px";
}

self.clearPolygons = function() {
    for (var i in self.mapleaflet._layers) {
        if (self.mapleaflet._layers[i]._path !==
            undefined) {
            try {
                self.mapleaflet.removeLayer(self.mapleaflet
                    ._layers[i]);
            } catch (e) {
                console.log("problem with " + e + self.mapleaflet
                    ._layers[i]);
            }

        }
    }

}
function parseJson(json) {
    var value = String(json).replace(/'/g, '\"');
    return JSON.parse(value);
}
self.showPolygons = function(unparsed, color) {
    try {
        geojsonFeature = parseJson(unparsed).features;
    } catch (err) {
        return;
        // geojsonFeature = null
    }
    var myStyle = {
        "color": color,
        "opacity": 0.8
    };
    try {
        L.geoJSON(geojsonFeature, {
            style: myStyle
        }).addTo(self.mapleaflet);
    } catch (e) {
        console.log(e);
    }

};

self.updateTimeRange = function() {
    var maxRange = 0;
    for (i = 0; i < self.ctx.data.length; i++) {
        var prop = self.ctx.data[i].data[0][1];
        // console.log(prop);
        for (j = 0; j < prop.length; j++) {
            if (prop[j].index > maxRange) {
                maxRange = prop[j].index;
            }
        }
    }
    $('#TimeFrame')[0].max = maxRange;
};

function findCurrFrame(frames, index) {
    if (frames instanceof Array) {
        for (var i = 0; i < frames.length; ++i) {
            if (parseInt(frames[i].index) === index) {
                return frames[i].value;
            }
        }
    }
    return undefined;
}

self.onDataUpdated = function() {
    self.updateTimeRange();
    var index = $('#TimeFrame')[0].value;
    self.clearPolygons();
    for (i = 0; i < self.ctx.data.length; i++) {
        var poly = findCurrFrame(self.ctx.data[index]);
        if (!poly) continue;
        var color = self.ctx.data[i].dataKey.color;
        self.showPolygons(poly, color);
    }
    self.ctx.map.update();
}
self.onResize = function() {
    self.ctx.map.resize();
    // self.resizeMap();
}

self.getSettingsSchema = function() {
    var tbScheme = TbMapWidgetV2.settingsSchema(
        'openstreet-map');
    return tbScheme;
}
self.getDataKeySettingsSchema = function() {
    return TbMapWidgetV2.dataKeySettingsSchema(
        'openstreet-map');
}
self.actionSources = function() {
    return TbMapWidgetV2.actionSources();
}
self.onDestroy = function() {}

